INSERT INTO M_Contact (
	MCNTCT_SalesOrganizationCD,
	MCNTCT_CounterBrandCD,
	MCNTCT_ContactCD,
	MCNTCT_ContactName,
	MCNTCT_OrderDisplay,
	MCNTCT_ActiveFlg,
	MCNTCT_FlgChangeDate,
	MCNTCT_Note,
	MCNTCT_UpdateEmployeeCD,
	MCNTCT_UpdateDatetime
) OUTPUT INSERTED.MCNTCT_ContactCD
SELECT
	@salesOrganizationCD,
	@counterBrandCD,
	(
		SELECT ISNULL(MAX(MCNTCT_ContactCD),0)+1 FROM M_Contact WHERE
			MCNTCT_SalesOrganizationCD = @salesOrganizationCD
			AND MCNTCT_CounterBrandCD = @counterBrandCD
	),
	@contactName,
	@orderDisplay,
	@activeFlg,
	CONVERT(DATETIME,@flgChangeDate),
	@note,
	@updateEmployeeCD,
	GETDATE()

